import React, { useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import axios from 'axios';
import { Formik, Form, Field, ErrorMessage } from "formik"
import { useNavigate } from "react-router-dom";
import Modal from '@mui/material/Modal'
import Box from '@mui/material/Box'


function PatientsDetail () {
    let navigate = useNavigate(); 
    let { id } = useParams();
    const [patientObject, setPatientObject] = useState({});
    const [levels, setlevels] = useState([]);

    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);

    const initialValues = {
        bloodSugarLevel: 0,
        weight: 0,
        cholesterol: 0,
        hemoglobin: 0,
        systolicBloodPressure: 0,
        diastolicBloodPressure: 0,
    }

    useEffect(() => {
        axios.get(`http://localhost:3001/patientsinfo/byId/${id}`).then((response) => {
            setPatientObject(response.data);
            console.log(response.data);
    }); 
        axios.get(`http://localhost:3001/levels/${id}`).then((response) => {
            setlevels(response.data);
            console.log(response.data);
    }); 
}, []);

 

    return(
        <section>
            <div className="font-bold uppercase p-10">
                {patientObject.id} , {patientObject.firstName} , {patientObject.lastName}
            </div>
            <div className="flex flex-row flex-wrap" >
                {levels.map((level, key) => {
                    return(
                    <div className="p-10">
                        <div className="flex-col">
                            <p>{patientObject.createdAt}</p>
                            <p>
                                {level.bloodSugarLevel} | Blood Sugar Level
                            </p>
                            <p>
                                {level.weight} | Weight
                            </p>
                            <p>
                                {level.cholesterol} | Cholesterol Level
                            </p>
                            <p>
                                {level.hemoglobin} | Hemoglobin Level
                            </p>
                            <p>
                                {level.systolicBloodPressure} | Systolic Level
                            </p>
                            <p>
                                {level.diastolicBloodPressure} | Diastolic Level
                            </p>
                        </div>
                    </div>
                    )
                })}
            </div>
            <div>
                <button
                onClick={handleOpen}
                className="text-white bg-gradient-to-br from-purple-600 to-blue-500 hover:bg-gradient-to-bl focus:ring-4 focus:outline-none focus:ring-blue-300 dark:focus:ring-blue-800 font-medium rounded-lg text-sm px-5 py-2.5 text-center me-2 mb-2 float-left"
                >
                Add New Info
                </button>
                <Modal
                    className="absolute inset-0 w-1/2 h-1/6 mx-auto my-auto"
                    open={open}
                    onClose={handleClose}
                >
                    <Formik
                    >
                        <label>Blood Sugar Level:</label>
                        <Field 
                        id="inputCreateInfo" 
                        name="bloodSugarLevel" 
                        placeholder="mmol/L"
                        className="peer border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-2.5"
                        />
                    </Formik> 
                </Modal>
            </div>
        </section>
    )
}

export default PatientsDetail
