import React from 'react';
import axios from 'axios';
import { useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom";

export default function SearchPatients() {
    const [listOfPatients, setListofPatients] = useState([]);

     let navigate = useNavigate(); 

     const [data, setData] = useState([]);
     const [searchFilter, setSearchFilter] = useState([]);
     const [result, setResult] = useState("");

    useEffect(() => {
      // axios.get("http://localhost:3001/patientsinfo").then((response) => {
      //   setListofPatients(response.data);
      // })
          const fetchData = async ()=> {
      try{
          const resp= await axios.get('http://localhost:3001/patientsinfo');
          setData(resp.data);
          setSearchFilter(resp.data);
          console.log(data)
          console.log(searchFilter)
          }
          catch(err){
            throw new Error(err);
          }
        };
        fetchData();
    }, []);
  
    useEffect(() =>{
      const results = searchFilter.filter(resp =>
         resp(result)
      );
      setData(results)
    },[result])
    
    const onChange = (evt) => {
      setResult(evt.target.value);
      console.log(result)
      }


    return(
      <section>
        <div class="pt-10 pb-10">
          <input 
              type="text"
              placeholder="Search here ... "
              value={result}
              onChange={onChange}
          />
          {data.map((r,i) =>(
            <ul key={i}>
              <li>{r.name}</li>
            </ul>
            )
          )}
        </div>
        
        <table className='h-lvh w-5/6 overflow-auto table-auto border-collapse  border-slate-500 cursor-pointer '>
          <thead>
            <tr className="px-6 py-3 text-start font-medium text-gray-500 uppercase dark:text-neutral-500">
              <th>Id</th>
              <th>Name</th>
              <th>Sex</th>
              <th>Location</th>
              <th>PhoneNumber</th>
            </tr>
          </thead>
          <hr></hr>
          <tbody className='cursor-pointer'>
            {listOfPatients.map((value, key) => {
              return(
                <tr onClick={() => navigate(`/patientsinfo/${value.id}`)}
                  className='odd:bg-white even:bg-gray-100 dark:odd:bg-neutral-000 dark:even:bg-gray-300 h-10 rounded-lg'
                >
                  <td className='p-10 '>{value.id}</td>
                  <td>{value.firstName} {value.lastName}</td>
                  <td>{value.sex}</td>
                  <td>{value.location}</td>
                  <td>{value.phoneNumber}</td>
                </tr>
              )
            })}
          </tbody>
        </table>
      </section>
     
    )
}
