import React from "react";
import DataView from 'react-datepicker'
import { Formik, Form, Field, ErrorMessage} from "formik"
import * as Yup from 'yup'
import "react-datepicker/dist/react-datepicker.css";
import FormikControl from "./FormikControl";


function FormikContainer () {
    const dropdownOptions = [
        {key: 'Select an option', value: ''},
        {key: 'Option 1', value:'option1'},
        {key: 'Option 2', value:'option2'},
        {key: 'Option 3', value:'option3'}
    ]  

    const radioOptions = [
        {key: 'Option 1', value:'rOption1'},
        {key: 'Option 2', value:'rOption2'},
        {key: 'Option 3', value:'rOption3'}
    ]

    const radioOptions2 = [
        {key: 'Option 1', value:'rOption1'},
        {key: 'Option 2', value:'rOption2'},
        {key: 'Option 3', value:'rOption3'}
    ]

    const initialValues = {
        firstName: "",
        lastName: "",
        sex: "",
        location: "Sale",
        phoneNumber: "",
        age: "",
        smoking: "",
        date: "",
    }

    const validationSchema = Yup.object({
        firstName: Yup.string().required("First Name requried*"),
        lastName:Yup.string().required("Last Name requried*"),
        sex: Yup.string().required("Select Male or Female"),
        location: Yup.string().required("Must be answered"),
        phoneNumber: Yup.string().required(),
        age: Yup.string().required("Must be answered"),
        smoking: Yup.string().required("Must be answered"),
        date: Yup.string().required(),
    })

    const onSubmit = values => console.log ("Form data", values)
    return(
        <Formik
            initialValues={initialValues}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
        >
            {
            formik => 
            <Form>
                <FormikControl control='input' type='firstName' label='firstName' name="firstName"/>
                <FormikControl control='input' type='lastName' label='lastName' name="lastName"/>
                <FormikControl control='input' type='phoneNumber' label='phoneNumber' name="phoneNumber"/>
                <FormikControl control='input' type='age' label='age' name="age"/>
                <FormikControl control='textarea' label='comment' name='comment'/>
                <FormikControl control='select' label='select a location' name='location' options={dropdownOptions}/>
                <FormikControl control='radio' label='Sex' name='sex' options={radioOptions} />
                <FormikControl control='radio' label='Smoking' name='smoking' options={radioOptions2} />
                <FormikControl control='date' label='Pick a Date' name='date' />
                <button type='submit'>Submit</button>
            </Form>
            }
        </Formik>
    )
}

export default FormikContainer
