import React, { useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import axios from 'axios';

function PatientsDetail () {
    let { id } = useParams();
    const [patientObject, setPatientObject] = useState({});
    const [glucoses, setGlucoses] = useState({})

    useEffect(() => {
        axios.get(`http://localhost:3001/patientsinfo/byId/${id}`).then((response) => {
            setPatientObject(response.data);
    });

        axios.get(`http://localhost:3001/bloodsugarlevel/byId/${id}`).then((response) => {
            setGlucoses(response.data);
    });


}, []);


    return(
        <section>
            <div>
                {patientObject.firstName} , {patientObject.lastName}
            </div>
            <div>
                {glucose.map((comment, key) => {
                    return <div> {glucose.bloodSugarLevel} </div>
                })}
            </div>
            <h1>
                Add Info
            </h1>
        </section>
    )
}

export default PatientsDetail
