import React, { useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import axios from 'axios';
import { Formik, Form, Field, ErrorMessage } from "formik"
import { useNavigate } from "react-router-dom";
import { useFormik } from "formik";
import Modal from '@mui/material/Modal'
import Box from '@mui/material/Box'


function PatientsDetail () {
    let navigate = useNavigate(); 
    let { id } = useParams();
    const [patientObject, setPatientObject] = useState({});
    const [levels, setlevels] = useState([]);

    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => 
        
        setOpen(false);

    const initialValues = { 
        bloodSugarLevel: 0,
        weight: 0,
        cholesterol: 0,
        hemoglobin: 0,
        systolicBloodPressure: 0,
        diastolicBloodPressure: 0,
        InfoId:id,
        
    }

    const onSubmit = (data) => {
        axios.post("http://localhost:3001/levels", data).then((response) => {
            console.log(response.data)
            console.log(initialValues)
            console.log("it worked")
            // window.location.reload(false)
        })
    }


    useEffect(() => {
        axios.get(`http://localhost:3001/patientsinfo/byId/${id}`).then((response) => {
            setPatientObject(response.data);
            console.log(response.data);
    }); 
        axios.get(`http://localhost:3001/levels/${patientObject.id}`).then((response) => {
            setlevels(response.data);
            console.log(response.data);
    }); 
}, []);

    const deletePatient = (id) => {
        axios.delete(`http://localhost:3001/patientsinfo/${id}`)
        .then(() => {
            console.log(`Deleted post with ID ${patientObject.id}`);
            navigate(`/searchPatients/`)
            // window.location.reload(false)
        })
    }

    const deleteLevel = (id) => {
        axios.delete(`http://localhost:3001/levels/${id}`)
        .then(()=>{
            console.log(`Deleted post with ID ${patientObject.id}`);
            navigate(`/searchPatients/`)
            window.location.reload(false)
        })
    }
    return(
        <section className="max-w-full max-h-full p-8">
            <div className="font-bold uppercase">
                {patientObject.id} , {patientObject.firstName} , {patientObject.lastName}
            </div>
            <div className="font-bold uppercase p-10">
                {patientObject.sex} , {patientObject.location} , {patientObject.phoneNumber}
                {/* <button onClick={() => deletePatient(patientObject.id)}
                    className="text-white bg-gradient-to-br from-purple-600 to-blue-500 hover:bg-gradient-to-bl focus:ring-4 focus:outline-none focus:ring-blue-300 dark:focus:ring-blue-800 font-medium rounded-lg text-sm px-5 py-2.5 text-center me-2 mb-2"
>
                    Delete Patient
                </button> */}
            </div>
            <div className="flex flex-row">
                <Formik
                    initialValues={initialValues}
                    onSubmit={onSubmit}
                    >
                        <Form>
                            <div className="">
                            <label
                            className="uppercase"
                            >Blood Sugar Level</label>
                            <Field
                            id="inputCreateInfo" 
                            name="bloodSugarLevel" 
                            placeholder="mmol/L"
                            className="border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-1 mb-5"
                            />
                            </div>
                            <label
                            className="uppercase"
                            >Weight</label>
                            <Field
                            id="inputCreateInfo" 
                            name="weight" 
                            placeholder="kg"
                            className="border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-1  mb-5"

                            />
                            <label
                            className="uppercase"
                            >Cholesterol Level</label>
                            <Field
                            id="inputCreateInfo" 
                            name="cholesterol" 
                            placeholder="kg"
                            className="border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-1  mb-5"     
                            />

                            <label
                            className="uppercase"
                            >Hemoglobin Level</label>
                            <Field
                            id="inputCreateInfo" 
                            name="hemoglobin" 
                            placeholder="kg"
                            className="border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-1  mb-5"                            
                            />
                            
                            <label
                            className="uppercase"
                            >Systolic Level</label>
                            <Field
                            id="inputCreateInfo" 
                            name="systolicBloodPressure" 
                            placeholder="kg"
                            className="border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-1  mb-5"                            
                            />

                            <label
                            className="uppercase"
                            >Diastolic Level</label>
                            <Field
                            id="inputCreateInfo" 
                            name="diastolicBloodPressure" 
                            placeholder="kg"
                            className="border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-1  mb-5" 
                            />

                            <Field
                            value={id}
                            id="InfoId" 
                            name="InfoId" 
                            placeholder="mmol/L"
                            className="border-2 border-gray-300 text-gray-900 text-sm rounded-lg block p-1 mb-5"
                            />
                            <button type="submit"
                            className="text-white bg-gradient-to-br from-purple-600 to-blue-500 hover:bg-gradient-to-bl hover:border-slate-400 focus:ring-4 focus:outline-none focus:ring-blue-300 dark:focus:ring-blue-800 font-medium rounded-lg text-sm px-5 py-2.5 text-center me-2 mb-2 float-left"
                            >
                                Add Level
                            </button>
                        </Form>
                </Formik>
                <div className="flex flex-row flex-wrap" >
                    {levels.map((level, key) => {
                        return(
                        <div className="pl-10 pb-10">
                            <div className="flex-col">
                                <p>{level.id}</p>
                                <p>{level.createdAt}</p>
                                <p>
                                    {level.bloodSugarLevel} | Blood Sugar Level
                                </p>
                                <p>
                                    {level.weight} | Weight
                                </p>
                                <p>
                                    {level.cholesterol} | Cholesterol Level
                                </p>
                                <p>
                                    {level.hemoglobin} | Hemoglobin Level
                                </p>
                                <p>
                                    {level.systolicBloodPressure} / {level.diastolicBloodPressure} | Blood Pressure
                                </p>
                                <button 
                                onClick={() => deleteLevel(patientObject.id)}>
                                    x
                                </button>
                            </div>
                        </div>
                        )
                    })}
                </div>
            </div>
        </section>
    )
}

export default PatientsDetail
